mapper.select:
select *
from sys_bike
where rent_status = #{rent_status}
  and bike_status = #{bike_status}

select *
from sys_bike
where rent_status = #{rent_status}
  and bike_status = #{bike_status}
order by rent_count desc limit 5

select *
from sys_rent_bike
where bike_id = #{bikeId}
  and host_id = #{hostId}
  and rent_id = #{rentId}
  and ISNULL(return_date)

select count(*)
from sys_bike
where brand = #{s}

select rent_count
from sys_bike
where brand = #{s}
